import React, { useState } from "react";

const Bubble = ({ compras }) => {
  //calcula cantidad total
  let total = 0;

  const calcularC = () => {
    total>=
    compras.forEach((producto) => {
      total += producto.cantidad;
    });

  };
  calcularC();
  return (
    <div className="bubble">
      <p className="bubble__p">{total}</p>
    </div>
  );
};

export default Bubble;
