import React, { Component } from "react";
import NAV from "./componentes/NAV";
import TITLE from "./componentes/TITLE";
import CARDS from "./componentes/CARDS";
import PRODUCTOS from "./componentes/PRODUCTOS";

//import "./App.css";¨

import "./main.css";

class App extends Component {
  state = {
    productos: [
      {
        name: "Tomate",
        price: 1500,
        img: "/imgs/tomate.jpg",
      },

      {
        name: "Arebejas",
        price: 2500,
        img: "/imgs/arbejas.jpg",
      },

      {
        name: "Lechuga",
        price: 500,
        img: "/imgs/lechuga.jpg",
      },
    ],

    carro: [
      // {name: "Tomate", price: 1500,img: "/imgs/tomate.jpg",cantidad:1 },
    ],

    esCarroVisible: false,
  };

  agregarCarro = (producto) => {
    //validar
    const { carro } = this.state;

    const buscar = carro.find((el) => el.name === producto.name);

    if (buscar) {
      const newCarro = carro.map((el) =>
        el.name === producto.name
          ? {
              ...el,
              cantidad: el.cantidad + 1,
            }
          : el
      );

      return this.setState({ carro: newCarro });
    }

    return this.setState({
      carro: this.state.carro.concat({
        ...producto,
        cantidad: 1,
      }),
    });

    //le concatenamos al arreglo vacio de carro el producto que tengamos
    //mas propiedades exytas
  };

  mostrarCarro=()=>{
    this.setState({
      esCarroVisible:!this.state.esCarroVisible
    })
  }

  render() {
    console.log(this.state.carro);
    return (
      <div>
        <NAV carro={this.state.carro} />
        <PRODUCTOS  productos={this.state.carro} />
        <TITLE />
        <CARDS
          productos={this.state.productos}
          agregarCarro={this.agregarCarro}
        />
      </div>
    );
  }
}

export default App;
