import React, { Component } from "react";

class CARDS extends Component {
  render() {
    const { productos, agregarCarro } = this.props;

    return (
      <div className="center flexC">
        {productos.map((el) => (
          <div key={el.name} className="card">
            <img alt={el.name} src={el.img} className="card__img"></img>
            <h2 className="card__title">{el.name}</h2>
            <h6 className="card__price">{el.price}</h6>
            <button onClick={()=>agregarCarro(el)} className="card__btn">
              Agregar al carro
            </button>
          </div>
        ))}
      </div>
    );
  }
}
export default CARDS;

/*

pasamos propiedad de agregar al carro ya que este lo recibira 

*/
