import React, { Component } from "react";
import BUBBLE from "./BUBBLE";
class BOTON extends Component {
  render() {
    const { carro,esCarroVisible } = this.props;

    let sumaTotal = 0;

    carro.forEach((element) => {
      sumaTotal = sumaTotal + element.cantidad;
    });

    return (
      <div>
        <button onClick={esCarroVisible}  className="btn">Carro</button>;
        <BUBBLE value={sumaTotal} />
      </div>
    );
  }
}

export default BOTON;
