import React, { Component } from "react";
import Productos from "./componentes/Productos";
import Layout from "./componentes/Layout";
import Navbar from "./componentes/Navbar";
//import "./App.css";¨

import "./main.css";

class App extends Component {
  state = {
    productos: [
      {
        name: "Tomate",
        price: 1500,
        img: "/imgs/tomate.jpg",
      },

      {
        name: "Arebejas",
        price: 2500,
        img: "/imgs/arbejas.jpg",
      },

      {
        name: "Lechuga",
        price: 500,
        img: "/imgs/lechuga.jpg",
      },
    ],

    carro: [
      //{ name: "Tomate", price: 1500, img: "/imgs/tomate.jpg", cantidad: 1 },
    ],
  };

  agregarAlcarro = (producto) => {
    console.log(producto);
    alert("click");
  };

  render() {
    return (
      <div>
        <Navbar />
        <Layout>
          <Productos
            agregarAlcarro={this.agregarAlcarro}
            productos={this.state.productos}
          />
        </Layout>
      </div>
    );
  }
}

export default App;

