import React, { Component } from "react";

class BTNS extends Component {
  state = {
    cont: 0,
  };

  sumar = () => {
    this.setState({
      cont: this.state.cont + 1,
    });
  };

  restar = () => {
    if (this.state.cont <= 0) {
      alert("No puedes bajar mas de 0 !!");
      this.setState({
        cont: 0,
      });
    } else {
      this.setState({
        cont: this.state.cont - 1,
      });
    }
  };

  render() {
    return (
      <div className="center">
        <h2>{this.state.cont}</h2>
        <button onClick={this.sumar} className="btn3">
          +
        </button>
        <button onClick={this.restar} className="btn3">
          -
        </button>
      </div>
    );
  }
}

export default BTNS;
