import React, { Component } from "react";

class BOTON extends Component {
  state = {
    cont: 0,
  };

  suma = () => {
    this.setState({
      cont: this.state.cont + 1,
    });
  };

  resta = () => {

    if(this.state.cont)
    this.setState({
      cont: this.state.cont - 1,
    });
  };

  render() {
    return (
      <div className="center">
        <h2>{this.state.cont}</h2>
        <button className="btn" onClick={this.suma}>
          +
        </button>
        <button className="btn" onClick={this.resta}>
          -
        </button>
      </div>
    );
  }
}

export default BOTON;
