import React, { Component } from "react";
import BUBBLE from "./BUBBLE";
import PRODUCTOS from './PRODUCTOS';

class BOTON extends Component {
  render() {
    const { carro,visible,bandera } = this.props;

    let sumaTotal = 0;

    carro.forEach((element) => {
      sumaTotal = sumaTotal + element.cantidad;
    });

    return (
      <div>
        <button onClick={visible}  className="btn">Carro</button>;
        {bandera?<PRODUCTOS carro={carro}/>:null}
      </div>
    );
  }
}

export default BOTON;
